mapper.insert:
select max(id) from bc_campsite
INSERT into bc_campsite ( account, password, seller_name, seller_phone, email, crn, profile_image, camp_name, address, postcode, detail_address, camp_phone
) values ( #{account}, #{password}, #{seller_name}, #{seller_phone}, #{email}, #{crn}, #{profile_image}, #{camp_name}, #{address}, #{postcode}, #{detail_address}, #{camp_phone} )

