mapper.insert:

    select max(id) from bc_rent_user

insert into bc_rent_user(
    user_id, driver_license_number, driver_license_id, driver_age_id, driver_experience_id, driver_license_image
)values(
    #{user_id}, #{driver_license_number}, #{driver_license_id}, #{driver_age_id}, #{driver_experience_id}, #{driver_license_image}
)

insert into bc_reservation(
    rent_user_id, product_id, start_date, end_date, progress, cancellation_date
)values(
    #{rent_user_id}, #{product_id}, #{start_date}, #{end_date}, '예약접수', #{cancellation_date}
)

insert into bc_rental_external_inspection(
    reservation_id, front_view, passenger_front_view, passenger_rear_view, rear_view, driver_rear_view,
    driver_front_view
)values(
    #{reservation_id},#{front_view},#{passenger_front_view},#{passenger_rear_view},#{rear_view},#{driver_rear_view},
    #{driver_front_view}
)

insert into bc_return_external_inspection(
    reservation_id, front_view, passenger_front_view, passenger_rear_view, rear_view, driver_rear_view,
    driver_front_view
)values(
    #{reservation_id},#{front_view},#{passenger_front_view},#{passenger_rear_view},#{rear_view},#{driver_rear_view}
    #{driver_front_view}
)
