mapper.insert:
insert into bc_club_join_condition(
    club_id, start_year, end_year, gender
)values(
    #{club_id},#{start_year},#{end_year},#{gender}
)

        insert into bc_club_member(
	        club_id, 
            user_id, 
            role_id
        )values(
	        #{club_id}, 
            #{user_id}, 
            #{role_id} 
        )

mapper.select:
select * from bc_club_post
where club_id = #{club_id}
order by id desc;

select * from bc_club_member
where club_id = #{club_id}
and user_id = #{user_id}

mapper.insert:
        insert into bc_club_post_comment(
	        post_id, user_id, content
        )values(
            #{post_id}, #{user_id}, #{content}
        )

mapper.select:
select count(*) from bc_club_bookmark
where club_id = #{club_id}
and user_id = #{user_id}

mapper.delete:
delete from bc_club_bookmark
where club_id=#{club_id} 
and user_id=#{user_id}

mapper.select:
update bc_club_post
set read_count = read_count + 1
where id = #{id}

select count(*) from bc_club_post_like
where post_id = #{post_id}
and user_id = #{user_id}

mapper.delete:
delete from bc_club_post_like
where post_id = #{post_id}
and user_id = #{user_id}

mapper.select:
select count (*) from bc_club_post_comment_like
where comment_id = #{comment_id}    
and user_id = #{user_id}

mapper.delete:
delete from  bc_club_post_comment_like
where comment_id = #{comment_id}
and user_id = #{user_id}

mapper.insert:
    insert into bc_club_nested_comment(
	    comment_id, user_id, content
    )values(
	    #{comment_id}, #{user_id}, #{content}
    )

mapper.select:
select * from bc_club_member
