order by id desc


select count(*) from bc_club_member
where club_id = #{club_id}
and user_id = #{user_id}

        select * from bc_club_post_image
        where post_id in (
	        select id from bc_club_post
	        where club_id = #{club_id}
        )

        select * from bc_club
        where id in (
	        select club_id from bc_club_bookmark
	        where user_id = #{user_id}
        )

select * from bc_club_meeting
where club_id = #{club_id}
