and user_id = #{user_id}

mapper.select:
    SELECT * FROM bc_club_visit
	where club_id = #{club_id}
	and user_id = #{user_id}
	and DATE(visit_date) = CURDATE();

SELECT COUNT(*) FROM bc_club_visit
where club_id = #{club_id}
and DATE(visit_date) = CURDATE();

update bc_club_visit
set visit_count = visit_count + 1
where id = #{id}

mapper.update:
        UPDATE bc_club_member
        SET role_id = #{role_id}, 
	        updated_at = NOW()
        WHERE club_id = #{club_id}
        AND user_id = #{user_id}

mapper.select:
select * from bc_club_meeting
where meeting_date = #{meeting_date}
and club_id = #{club_id}

select * from bc_club_meeting_member bcmm
inner join bc_user bu on bcmm.user_id =bu.id
