mapper.insert:
insert into bc_club(
    user_id, region_id, name, description, capacity, main_image
)values(
    #{user_id}, #{region_id}, #{name}, #{description}, #{capacity}, #{main_image}
);

insert into bc_club_join_condition(
    club_id, min_age, max_age, gender
)values(
    #{club_id},#{min_age},#{max_age},#{gender}
);
